import React from "react";
import { decode } from "html-entities";
import ReactDOM from "react-dom";

export default function Question(props) {
  const element = props.questions.map((quiz) => {
    let answers = quiz.incorrect_answers;
    answers.insert(
      Math.ceil(Math.random() * answers.length + 1),
      quiz.correct_answer
    );
    const answerList = answers.map((answer) => (
      <label className="answer">{answer}</label>
    ));

    return (
      <form>
        <h2 className="question">{decodeEntities(quiz.question)}</h2>
        <div>{answerList}</div>
        <hr></hr>
      </form>
    );
  });
  return <div>{element}</div>;
}
