import React from "react";
import ReactDOM from "react-dom";

export default function Question(props) {
  function decodeHtml(html) {
    let areaElement = document.createElement("textarea");
    areaElement.innerHTML = html;

    return areaElement.value;
  }

  var decodeEntities = (function() {
    // this prevents any overhead from creating the object each time
    var element = document.createElement("div");

    function decodeHTMLEntities(str) {
      if (str && typeof str === "string") {
        // strip script/html tags
        str = str.replace(/<script[^>]*>([\S\s]*?)<\/script>/gim, "");
        str = str.replace(/<\/?\w(?:[^"'>]|"[^"]*"|'[^']*')*>/gim, "");
        element.innerHTML = str;
        str = element.textContent;
        element.textContent = "";
      }

      return str;
    }

    return decodeHTMLEntities;
  })();

  const element = props.questions.map((quiz) => (
    <form>
      <h2 className="question">{quiz.question}</h2>
      <div>
        <label className="answer">{quiz.correct_answer}</label>
        <label className="answer">{quiz.incorrect_answers[0]}</label>
        <label className="answer">{quiz.incorrect_answers[1]}</label>
        <label className="answer">{quiz.incorrect_answers[2]}</label>
      </div>
      <hr></hr>
    </form>
  ));
  return <div>{element}</div>;
}
