import React from "react";
import ReactDOM from "react-dom";

export default function Question(props) {
  function decodeHtml(html) {
    let areaElement = document.createElement("textarea");
    areaElement.innerHTML = html;

    return areaElement.value;
  }
  const element = props.questions.map((quiz) => (
    <form>
      <h2 className="question">{quiz.question}</h2>
      <div>
        <label className="answer">{quiz.correct_answer}</label>
        <label className="answer">{quiz.incorrect_answers[0]}</label>
        <label className="answer">{quiz.incorrect_answers[1]}</label>
        <label className="answer">{quiz.incorrect_answers[2]}</label>
      </div>
      <hr></hr>
    </form>
  ));
  return <div>{decodeHtml(element)}</div>;
}
