import React from "react";
import ReactDOM from "react-dom";

export default function Question(props) {
  Array.prototype.insert = function(index, item) {
    this.splice(index, 0, item);
  };
  var decodeEntities = (function() {
    // this prevents any overhead from creating the object each time
    var element = document.createElement("div");

    function decodeHTMLEntities(str) {
      if (str && typeof str === "string") {
        // strip script/html tags
        str = str.replace(/<script[^>]*>([\S\s]*?)<\/script>/gim, "");
        str = str.replace(/<\/?\w(?:[^"'>]|"[^"]*"|'[^']*')*>/gim, "");
        element.innerHTML = str;
        str = element.textContent;
        element.textContent = "";
      }

      return str;
    }

    return decodeHTMLEntities;
  })();

  // console.log(decodeEntities(props.questions[0].question));

  const element = props.questions.map((quiz) => (
    <form>
      <h2 className="question">{decodeEntities(quiz.question)}</h2>
      <div>
        {() => {
          let answers = quiz.incorrect_answers;
          answers.insert(Math.ceil(Math.random() * 3), quiz.correct_answer);
          return answers.map((answer) => (
            <label className="answer">{answer}</label>
          ));
        }}
      </div>
      <hr></hr>
    </form>
  ));
  return <div>{element}</div>;
}
