import React from "react";
import { decode } from "html-entities";
import ReactDOM from "react-dom";
import { random } from "nanoid";

Array.prototype.insert = function(index, item) {
  return this.splice(index, 0, item);
};

export default function Question(props) {
  //render question list.
  const element = props.questions.map((question) => {
    // render answer list
    const elementAnswer = question.incorrect_answers.map((answer) => {
      if (!props.isGameEnd)
        return (
          <div
            className="answer"
            onClick={() => props.selectAnswer(answer, question.id)}
            style={{
              backgroundColor:
                question.selectedAnswer === answer ? "#D6DBF5" : "",
              border:
                question.selectedAnswer === answer
                  ? "none"
                  : "1px solid #293264",
            }}
          >
            {decode(answer)}
          </div>
        );
      else
        return (
          <div
            className="answer"
            onClick={() => props.selectAnswer(answer, question.id)}
            style={{
              backgroundColor: () => {
                if (
                  question.selectedAnswer === answer &&
                  question.selectAnswer === question.correct_answer
                )
                  return "#94D7A2";
                else if (
                  question.selectedAnswer === answer &&
                  question.selectAnswer !== question.correct_answer
                )
                  return "#F8BCBC";
              },

              border:
                question.selectedAnswer === answer
                  ? "none"
                  : "1px solid #293264",
            }}
          >
            {decode(answer)}
          </div>
        );
    });

    return (
      <div className="container--question" key={question.id}>
        <h1 className="question">{decode(question.question)}</h1>
        <div className="container--answer">{elementAnswer}</div>
        <hr></hr>
      </div>
    );
  });
  return <div>{element}</div>;
}
