import React from "react";
import { decode } from "html-entities";
import ReactDOM from "react-dom";
import { random } from "nanoid";

Array.prototype.insert = function(index, item) {
  return this.splice(index, 0, item);
};

export default function Question(props) {
  const element = props.questions.map((question) => {
    console.log(question);
    const num = Math.ceil(Math.random() * question.incorrect_answers.length);
    question.incorrect_answers.insert(num, question.correct_answer);
    const answerList = question.incorrect_answers;
    const elementAnswer = answerList.map((answer) => (
      <div className="answer">{decode(answer)}</div>
    ));
    console.log(answerList);
    return (
      <div className="container--question" key={question.id}>
        <h1 className="question">{decode(question.question)}</h1>
        <div className="container--answer">{elementAnswer}</div>
        <hr></hr>
      </div>
    );
  });
  return <div>{element}</div>;
}
