import React from "react";
import { decode } from "html-entities";
import ReactDOM from "react-dom";
import { random } from "nanoid";

Array.prototype.insert = function(index, item) {
  this.splice(index, 0, item);
};

export default function Question(props) {
  const element = props.questions.map((question) => {
    console.log(question);
    const randomIndex = Math.ceil(
      Math.random() * question.incorrect_answers.length
    );

    let answers = question.incorrect_answers.insert(randomIndex);
    console.log(answers, randomIndex, question.correct_answer);
    // question.incorrect_answers.map();

    return (
      <div key={question.id}>
        <h1 className="question">{decode(question.question)}</h1>
      </div>
    );
  });
  return <div>{element}</div>;
}
