import React, { useEffect } from "react";
import ReactDom from "react-dom";
import { nanoid } from "nanoid";
import Die from "./components/Die";

/**
 * Challenge: Check the dice array for these winning conditions:
 * 1. All dice are held, and
 * 2. all dice have the same value
 *
 * If both conditions are true, set `tenzies` to true and log
 * "You won!" to the console
 */

export default function App() {
  const [dices, setDices] = React.useState(allNewDice());
  const [tenzies, setTenzies] = React.useState(false);

  React.useEffect(
    function () {
      let win = true;
      for (let i = 0; i < dices.length; i++) {
        if (!dices[i].isHeld) win = false;
        if (dices[i].value !== dices[i + 1].value) win = false;
      }
      dices.map((dice) => {
        !dice.isHeld ? (win = false) : "";
      });
      win ? console.log("You won") : console.log("Dice state changed");
    },
    [dices]
  );
  function allNewDice() {
    let arr = [];
    for (let i = 0; i < 10; i++) {
      arr[i] = {
        id: nanoid(),
        value: Math.floor(Math.random() * 6) + 1,
        isHeld: false,
      };
    }
    return arr;
  }

  function rollDice() {
    setDices((preDices) => {
      return preDices.map((dice) => {
        if (dice.isHeld) return dice;
        else return { ...dice, value: Math.floor(Math.random() * 6) + 1 };
      });
    });
  }

  function holdDice(id) {
    setDices((preDices) => {
      return preDices.map((dice) => {
        if (dice.id === id)
          return {
            ...dice,
            isHeld: !dice.isHeld,
          };
        else return dice;
      });
    });
  }

  return (
    <main>
      <div className="box">
        <div>
          <h3>Tenzies</h3>
          <p>
            Roll until all dice are the same. Click each die to freeze it at its
            current value between rolls.
          </p>
        </div>
        <div>
          <Die dices={dices} holdDice={holdDice} />
        </div>
        <button onClick={rollDice}>Roll</button>
      </div>
    </main>
  );
}
